from string import digits, ascii_uppercase

def convert(num, sys):
    r = ''
    alph = digits + ascii_uppercase
    while num:
        r += alph[num%sys]
        num //= sys
    return r[::-1]

N = 3*15**1140 + 2*15**1025 + 15**923 - 3*15**85 + 2*15**74 + 3
s = convert(N, 15)
print(s)
print(len(''))

